numbers = []q = int(input('Введите кол-во чисел которые вы хотите ввести: '))for k in range(q):    g = int(input('Введите число: '))    numbers.append(g)a = []for number in numbers:    count = 0    for n in str(number):        if int(n) % 2 == 0:            count += 1    if count == 2:        a.append(number)if len(a) <= 3:    print('Cписок чисел удовлетворяет условию.', a)else:    print('Cписок чисел не удовлетворяет условию.', a)