x = float(input('Введите координату х: '))y = float(input('Введите координату у: '))n = int(input('Введите размер квадрата: '))if n / 2 >= abs(x) :    if n / 2 >= abs(y):        print(f'Точка ({x, y}) попадает в квадрат с размером {n}')    else:        print(f'Точка ({x, y}) не попадает в квадрат с размером {n}')else:    print(f'Точка ({x, y}) не попадает в квадрат с размером {n}')